```{.r .cell-code}
birds_in_holc <- st_join(bird_data, LA_redlining, join = st_intersects, left = FALSE)

summary_birds_holc <- birds_in_holc %>% 
  group_by(grade) %>% 
  summarize(count = n()) %>% 
  mutate(percentage = (count/sum(count)) *100) %>% 
  st_drop_geometry() %>% 
  na.omit()

gt(summary_birds_holc)
```

